
public class App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 1+2;
		int mod = 13%2;
		int y = 10;
		System.out.println(x);
		System.out.println(--y);
		System.out.println(y--);
		System.out.println(y++);
		System.out.println(++y);
		System.out.println(mod);
		
	}

}
